import React, { useState, useEffect } from "react";
import './App.css';

function App() {
  // Definindo tempos em minutos para fácil modificação
  const WORK_TIME = 25;
  const SHORT_BREAK = 5;
  const LONG_BREAK = 15;

  const [minutes, setMinutes] = useState(WORK_TIME); // Tempo inicial
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false); // Estado do temporizador
  const [currentMode, setCurrentMode] = useState("work"); // Modo atual: "work", "shortBreak", ou "longBreak"

  useEffect(() => {
    let interval;

    if (isActive) {
      interval = setInterval(() => {
        if (seconds === 0) {
          if (minutes === 0) {
            // Alterna entre modos ao final do tempo
            switch (currentMode) {
              case "work":
                setCurrentMode("shortBreak");
                setMinutes(SHORT_BREAK);
                break;
              case "shortBreak":
                setCurrentMode("work");
                setMinutes(WORK_TIME);
                break;
              case "longBreak":
                setCurrentMode("work");
                setMinutes(WORK_TIME);
                break;
              default:
                break;
            }
            setSeconds(0);
          } else {
            setMinutes(minutes - 1);
            setSeconds(59);
          }
        } else {
          setSeconds(seconds - 1);
        }
      }, 1000);
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isActive, seconds, minutes, currentMode]);

  // Função para iniciar/parar o temporizador
  const startStopTimer = () => {
    setIsActive(!isActive);
  };

  // Função para resetar o temporizador ao tempo de trabalho
  const resetTimer = () => {
    setIsActive(false);
    setCurrentMode("work");
    setMinutes(WORK_TIME);
    setSeconds(0);
  };

  // Função para ativar a pequena pausa
  const startShortBreak = () => {
    setIsActive(false); // Pausa temporizador ativo
    setCurrentMode("shortBreak"); // Define modo de pequena pausa
    setMinutes(SHORT_BREAK); // Ajusta minutos para a pequena pausa
    setSeconds(0);
    setIsActive(true); // Inicia contagem
  };

  // Função para ativar a longa pausa
  const startLongBreak = () => {
    setIsActive(false);
    setCurrentMode("longBreak");
    setMinutes(LONG_BREAK);
    setSeconds(0);
    setIsActive(true);
  };

  return (
    <div className="App">
      <h1>
        {currentMode === "work" ? "Tempo de Trabalho" : currentMode === "shortBreak" ? "Pequena Pausa" : "Longa Pausa"}
      </h1>
      <div className="timer">
        <span>{String(minutes).padStart(2, "0")}</span>:
        <span>{String(seconds).padStart(2, "0")}</span>
      </div>
      <div className="controls">
        <button onClick={startStopTimer}>
          {isActive ? "Pausar" : "Iniciar"}
        </button>
        <button onClick={resetTimer}>Reiniciar</button>
        <button onClick={startShortBreak}>Pequena Pausa</button>
        <button onClick={startLongBreak}>Longa Pausa</button>
      </div>
      <footer className="footer">
    <div className="footer-links">
      <a href="#home">Home</a>
      <a href="#privacy">Privacy</a>
      <a href="#terms">Terms</a>
      <a href="#contact">Contact</a>
    </div>

    <div className="footer-icons">
      <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
        <i className="fab fa-facebook-f"></i>
      </a>
      <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
        <i className="fab fa-twitter"></i>
      </a>
      <img src="stripe-climate-badge.png" alt="Stripe Climate Badge" className="badge" />
    </div>

    <p className="footer-text">Feito por:&lt;3 Davi Felipe</p>
    <p className="footer-copyright">©PomodoroX 2024. All Rights Reserved.</p>
  </footer>
    </div>
  );
}


export default App;
