import React, { useState, useEffect } from 'react';
import './Settings.css';

const Settings = ({ onClose }) => {
  // Definindo os estados para os tempos
  const [pomodoroTime, setPomodoroTime] = useState(25);
  const [shortBreakTime, setShortBreakTime] = useState(5);
  const [longBreakTime, setLongBreakTime] = useState(15);
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Carregando configurações anteriores (se existirem) do localStorage
  useEffect(() => {
    const savedSettings = JSON.parse(localStorage.getItem('settings'));
    if (savedSettings) {
      setPomodoroTime(savedSettings.pomodoroTime || 25);
      setShortBreakTime(savedSettings.shortBreakTime || 5);
      setLongBreakTime(savedSettings.longBreakTime || 15);
      setIsDarkMode(savedSettings.isDarkMode || false);
    }
  }, []);

  // Salvando configurações no localStorage
  useEffect(() => {
    const settings = { pomodoroTime, shortBreakTime, longBreakTime, isDarkMode };
    localStorage.setItem('settings', JSON.stringify(settings));
  }, [pomodoroTime, shortBreakTime, longBreakTime, isDarkMode]);

  // Função para controlar a alteração do tempo
  const handleTimeChange = (e, timeType) => {
    const value = Number(e.target.value); // Garantindo que o valor seja numérico
    if (timeType === 'pomodoro') setPomodoroTime(value);
    if (timeType === 'shortBreak') setShortBreakTime(value);
    if (timeType === 'longBreak') setLongBreakTime(value);
  };

  return (
    <div className={`settings-panel ${isDarkMode ? 'dark' : ''}`}>
      <button className="close-btn" onClick={onClose}>✖</button>
      <h2 className="settings-title">Configurações</h2>

      <div className="setting-group">
        <h3>Tempo Pomodoro</h3>
        <input
          type="number"
          value={pomodoroTime}
          onChange={(e) => handleTimeChange(e, 'pomodoro')}
          min="1"
        />
      </div>

      <div className="setting-group">
        <h3>Tempo de Pausa Curta</h3>
        <input
          type="number"
          value={shortBreakTime}
          onChange={(e) => handleTimeChange(e, 'shortBreak')}
          min="1"
        />
      </div>

      <div className="setting-group">
        <h3>Tempo de Pausa Longa</h3>
        <input
          type="number"
          value={longBreakTime}
          onChange={(e) => handleTimeChange(e, 'longBreak')}
          min="1"
        />
      </div>

      <div className="setting-group">
        <h3>Tema Escuro</h3>
        <label>
          <input
            type="checkbox"
            checked={isDarkMode}
            onChange={() => setIsDarkMode(!isDarkMode)}
          />
          Ativar
        </label>
      </div>
    </div>
  );
};

export default Settings;
